import { ReactComponent as SwearIcon } from '../icons/swear.svg';
import { ReactComponent as NoteIcon } from '../icons/note.svg';
import { ReactComponent as GroupIcon } from '../icons/group.svg';
import { ReactComponent as ProfileIcon } from '../icons/profile.svg';
import {CSSTransition} from 'react-transition-group'
import { useState } from 'react';
export function DropdownMenu() {
    const [activeMenu, setActivateMenu] = useState('menu')
    function DropdownItem(props) {
        return (
            <a href="#" className="menu-item" onClick={() => {
                props.goToMenu && setActivateMenu(props.goToMenu)
            }}>
                <span className="icon-button">{props.leftIcon}</span>
                {props.children}
                <span className="icon-right">{props.rightIcon}</span>
            </a>
        )
    }
    return (
        <div className="dropdown">
            <div className='main'>
                <CSSTransition in={activeMenu === 'main'} unmountOnExit timeout={500} classNames='menu-primary'>            
                <div className='menu'>
                    <DropdownItem>My Profile</DropdownItem>
                    <DropdownItem 
                    leftIcon={<SwearIcon/>}        
                    rightIcon={<ProfileIcon/>}
                    goToMenu="settings">Settings</DropdownItem>
                    </div>
                </CSSTransition>
                <CSSTransition in={activeMenu === 'settings'} unmountOnExit timeout={500} classNames='menu-secondary'>            
                    <div className='menu'>
                        <DropdownItem>Settings</DropdownItem>
                    </div>
                </CSSTransition>
            </div>
        </div>
    );
}
