import React, {Component} from 'react'
import Draggable, {DraggableCore} from 'react-draggable';
import {graphql} from 'react-apollo'
import {flowRight as compose} from 'lodash';
import loader from "./loading.gif"
import {getMessagesQuery, updateMessageQuery} from "./queries/queries"

class LoadingScreen extends Component {
    render(){
        return <div className="loader" style={{height:'100vh', width:"100%"}}>
        <center>
            <img src={loader} height="50%" width="50%" alt=""/>
        </center>
         </div>
    }
}


class Message extends Component {
    constructor(props) {
        super(props);
        this.state = {
            id: "",
            title: "",
            content: "",
            userId: "",
            position: {
                x: 0,
                y: 0
            }
        }
    }
    async stopDraggingEvent(e, dragElement) {
        //graphql query
        console.log(this.state)
        console.log(dragElement.x)
        console.log(dragElement.y)
        this.props.updateMessageQuery({
            variables: {
                id: this.state.id,
                title: this.state.title, 
                content: this.state.content, 
                position: {
                    x: this.state.position.x + dragElement.x,
                    y: this.state.position.y + dragElement.y,
                }, 
            }
        })
    }
    render() {
        const message = this.props.message;
        this.setState({id: message.id, title: message.title, content: message.content, position: {x: message.position.x, y: message.position.y}})
        const newStyle = {
            position: "absolute",
            left: `${message.position.x}px`,
            top: `${message.position.y}px`
        };
        
        return (
        <Draggable key={message.id} onStop={this.stopDraggingEvent.bind(this)}>
            <div id={message.id} style={newStyle} className='message' key={message.id}>
                <div contenteditable className='title'>
                    {message.title}
                </div>
                <div contenteditable className='content'>
                    <p>
                        {message.content}
                    </p>
                    <p className='writer'>- {message.user.username}</p>
                </div>
            </div>
        </Draggable>
        )
    }
}
class Messages extends Component {
    
    readMessages() {
        console.log(this.props)
        if (this.props.getMessagesQuery.loading) {
            return <LoadingScreen/>
        }
        return this.props.getMessagesQuery.messages.map(message => {
            return <Message message={message} updateMessageQuery={this.props.updateMessageQuery}/>
        })
    }
    render() {
        return <div>
            {this.readMessages()}
        </div>
    }
}

export default compose(
    graphql(getMessagesQuery, {name: "getMessagesQuery"}),
    graphql(updateMessageQuery, {name: "updateMessageQuery"}),
    )(Messages);
