import React, {Component} from 'react'
import Draggable, {DraggableCore} from 'react-draggable';
import {gql} from 'apollo-boost'
import {graphql} from 'react-apollo'
import loader from "./loading.gif"

const getMessagesQuery = gql`
    {
        messages {
            title
            content
            id
            user {
                username
                id
            }
            position {
                x
                y
            }
        }
    }
`
class LoadingScreen extends Component {
    render(){
        return <div className="loader" style={{height:'100vh', width:"100%"}}>
        <center>
            <img src={loader} height="50%" width="50%" alt=""/>
        </center>
         </div>
    }
}


class Message extends Component {
    constructor() {
        super();
      
        this.state = {
          x: 0,
          y: 0
        };
    }
    _event = (function stopDraggingEvent(e, dragElement) {
        //send new position at dragElement.x, dragElement.y
        console.log(dragElement.x)
        this.setState({x: this.state.x + dragElement.x, y: this.state.y + dragElement.y});
    }).bind(this)
    render() {
        const message = this.props.message;
        
        this.setState({x: message.position.x, y: message.position.y})
        
        return (<Draggable key={message.id} onStop={this._event}><div id={message.id} style={{position: "absolute", left: `${this.state.x}px`, top: `${this.state.y}px`}} className='message' key={message.id}>
        <div className='title'>
            {message.title}
        </div>
        <div className='content'>
            <p>
                {message.content}
            </p>
            <p className='writer'>- {message.user.username}</p>
        </div>
        
        </div></Draggable>)
    }
}
class Messages extends Component {
    
    readMessages() {
        console.log(this.props)
        if (this.props.data.loading) {
            return <LoadingScreen/>
        }
        return this.props.data.messages.map(message => {
            return <Message message={message}/>
        })
    }
    render() {
        return <div>
            {this.readMessages()}
        </div>
    }
}

export default graphql(getMessagesQuery)(Messages);
