import {gql} from 'apollo-boost'

export const getMessagesQuery = gql`
    {
        messages {
            title
            content
            id
            user {
                username
                id
            }
            position {
                x
                y
            }
        }
    }
`
export const updateMessageQuery = gql`
    mutation(id: String!, $title: String, $content: String, position: {x: String, y: String}) {
        updateMessage(id=$id, title=$title, content=$content, position {
            x=$position.x, 
            y=$position.y
        })
    }
`
