import { ReactComponent as SwearIcon } from '../icons/swear.svg';
import { ReactComponent as NoteIcon } from '../icons/note.svg';
import { ReactComponent as GroupIcon } from '../icons/group.svg';
import { ReactComponent as ProfileIcon } from '../icons/profile.svg';

export function DropdownMenu() {
    function DropdownItem(props) {
        return (
            <a href="#" className="menu-item">
                <span className="icon-button">{props.leftIcon}</span>
                {props.children}
                <span className="icon-right">{props.rightIcon}</span>
            </a>
        )
    }
    return (
        <div className="dropdown">
            <DropdownItem>My Profile</DropdownItem>
            <DropdownItem 
            leftIcon={<SwearIcon/>}        
            rightIcon={<ProfileIcon/>}>Settings</DropdownItem>
        </div>
    );
}
