import React, {Component, useEffect} from 'react'
import {gql} from 'apollo-boost'
import {graphql} from 'react-apollo'
import loader from "./loading.gif"

const getMessagesQuery = gql`
    {
        messages {
            title
            content
            id
            user {
                username
                id
            }
            position {
                x
                y
            }
        }
    }
`
class LoadingScreen extends Component {
    render(){
        return <div className="loader" style={{height:'100vh', width:"100%"}}>
        <center>
            <img src={loader} height="75%" width="75%" center alt=""/>
        </center>
         </div>
    }
}


class Messages extends Component {
    readMessages() {
        console.log(this.props)
        if (this.props.data.loading) {
            return <LoadingScreen/>
        }
        return this.props.data.messages.map(message => {
            const newStyle = {
                position: "absolute",
                left: `${message.position.x}px`,
                top: `${message.position.y}px`
            }
            return (<div style={newStyle} className='message' key={message.id}>
                <div className='title'>
                    {message.title}
                </div>
                <div className='content'>
                    <p>
                        {message.content}
                    </p>
                    <p className='writer'>- {message.user.username}</p>
                </div>
                
                </div>)
        })
    }
    render() {
        return <div>
            {this.readMessages()}
        </div>
    }
}

export default graphql(getMessagesQuery)(Messages);
