import React, {Component, useState, signal} from 'react'
import {LoadingScreen} from './LoadingScreen'
import {Message} from './Message'
import {graphql} from 'react-apollo'
import {flowRight as compose} from 'lodash';
import {getMessagesQuery, updateMessageQuery} from "../queries/queries"


function Messages(props) {
    const messages = signal([])
    function readMessages() {
        console.log(props)
        if (props.getMessagesQuery.loading) {
            return <LoadingScreen/>
        }
        if(messages.length == 0) {
            var a = props.getMessagesQuery.messages.map(message => {
                return <Message message={message} messages={messages} setMessages={setMessages} updateMessageQuery={props.updateMessageQuery}/>
            })
            console.log(a)
            setMessages(a)
        }
        return messages
    }
    return <div>
        {readMessages()}
    </div>

}
export default compose(
    graphql(getMessagesQuery, {name: "getMessagesQuery"}),
    graphql(updateMessageQuery, {name: "updateMessageQuery"}),
)(Messages);
