import React, {Component} from 'react'
import {gql} from 'apollo-boost'
import {graphql} from 'react-apollo'
import loder from "./jest_logo.png"

const getMessagesQuery = gql`
    {
        messages {
            title
            content
            id
            user {
                username
                id
            }
        }
    }
`
class LoadingScreen extends Component {
    render(){
        return <div style={{height:'100vh', width:"100%"}}>
            <br/><br/><br/><br/><br/><br/><br/><br/><br/>  
        <center>
        <img src={loder} height="200" alt=""/></center>
         </div>
    }
}
class MessageList extends Component {
    readMessages() {
        console.log(this.props)
        if (this.props.data.loading) {
            return <LoadingScreen/>
        }
        return <ul className='messages'>
                {this.props.data.messages.map(message => {
            return (<li className='message' key={message.id}>
                <div className='title'>
                    {message.title}
                </div>
                <div className='content'>
                    <p>
                        {message.content}
                    </p>
                    <p className=''>- {message.user.username}</p>
                </div>
                
                </li>)
        })}
            </ul>
    }
    render() {
        return <div>
            {this.readMessages()}
        </div>
    }
}

export default graphql(getMessagesQuery)(MessageList);
