import React, {Component, useState} from 'react'
import Draggable, {DraggableCore} from 'react-draggable';

function startDraggingEvent(e) {
    // const message_id = this.details.id;
    // var temp_messages = this.messages
    // var index = 0;
    // for(index = 0; index < temp_messages.length; index++) 
    //     if (temp_messages[index].id != message_id)
    //         index++;
    //     else 
    //         break;
    // temp_messages.unshift(temp_messages.splice(index, 1)[0]);
    // this.setMessages(temp_messages)
}

function stopDraggingEvent(e, dragElement, props, details) {
    props.updateMessageQuery({
        variables: {
            id: details.id,
            position: {
                x: details.position.x + dragElement.x,
                y: details.position.y + dragElement.y,
            }, 
        }
    })
}

export function Message(props){
    console.log(props)
    const [details, setDetails] = useState({
        id: "",
        title: "",
        content: "",
        user: {
            username: ""
        },
        position: {
            x: 0,
            y: 0
        }
    })
    const message = props.message;
    console.log("-------------------------" )
    console.log(message.id != details.id )
    console.log(message.position != details.position )
    console.log(message.title != details.title )
    console.log(message.content != details.content)
    
    
    if(message.id != details.id || message.position.x != details.position.x || message.position.y != details.position.y || message.title != details.title || message.content != details.content)
        setDetails(prevState => ({
            ...prevState, 
            id: message.id, title: message.title, content: message.content, user: {username: message.user.username}, position: {x: message.position.x, y: message.position.y}
        }))
    const newStyle = {
        position: "absolute",
        left: `${details.position.x}px`,
        top: `${details.position.y}px`
    };

    const setMessages = props.setMessages
    const messages = props.messages
        
    return (
    <Draggable key={details.id} onStop={stopDraggingEvent.bind(this)} onStart={(e, dragElement, props, details) => startDraggingEvent}>
        <div id={details.id} style={newStyle} className='message' key={details.id}>
            <div className='title'>
                {details.title}
            </div>
            <div className='content'>
                <p>
                    {details.content}
                </p>
                <p className='writer'>- {details.user.username}</p>
            </div>
        </div>
    </Draggable>
    )
    
}
