import React, {Component, useState} from 'react'
import {LoadingScreen} from './LoadingScreen'
import {Message} from './Message'
import {graphql} from 'react-apollo'
import {flowRight as compose} from 'lodash';
import {getMessagesQuery, updateMessageQuery} from "../queries/queries"


function Messages() {
    const [messages, setMessages] = useState([])
    function readMessages() {
        console.log(props)
        if (this.props.getMessagesQuery.loading) {
            return <LoadingScreen/>
        }
        setMessages(this.props.getMessagesQuery.messages.map(message => {
            return <Message message={message} messages={messages} setMessages={setMessages} updateMessageQuery={this.props.updateMessageQuery}/>
        }))
    }
    return <div>
        {readMessages()}
    </div>

}
export default compose(
    graphql(getMessagesQuery, {name: "getMessagesQuery"}),
    graphql(updateMessageQuery, {name: "updateMessageQuery"}),
)(Messages);
