import React, {useState} from "react"
export function NavItem(props) {
    const [open, setOpen] = useState(false)
    return (
      <li key={props.icon} className='nav-item'>
        <a className='icon-button' href="#" onClick={() => {setOpen(!open)}}>
          {props.icon}
        </a>
        {open && props.children}
      </li>
    )
  }
