import React, {Component} from 'react'
import {gql} from 'apollo-boost'
import {graphql} from 'react-apollo'


const getMessagesQuery = gql`
    {
        messages {
            title
        }
    }
`
class LoadingScreen extends Component {
    render(){
        return <div style={{background: "#F7D036", height:'100%', width:"100%"}}>Loading Screen</div>
    }
}
class MessageList extends Component {
    readMessages() {
        console.log(this.props)
        if (this.props.data.loading) {
            this.props.data.refetch();
            return <LoadingScreen/>
        }
        return <ul id='messages'>
                {this.props.data.messages.map(message => {
            return <li key={message.id}>{message.title}</li>
        })}
            </ul>
    }
    render() {
        return <div>
            {this.readMessages()}
        </div>
    }
}

export default graphql(getMessagesQuery)(MessageList);
